mapper.update:
            UPDATE edu_video_upload SET
	            title=#{title},
	            classifyOne=#{classifyOne},
	            classifyTwo=#{classifyTwo},
	            info=#{info}
				where id =#{id}

mapper.select:
SELECT count(1) FROM edu_video_upload left join edu_video_classify as classsifyOne on classsifyOne.id=edu_video_upload.classifyOne left join edu_video_classify as classsifyTwo on classsifyTwo.id=edu_video_upload.classifyTwo WHERE edu_video_upload.status=0
and edu_video_upload.create_time >=concat( #{e.startTime},'00:00:00')
and edu_video_upload.create_time <=concat( #{e.endTime},'23:59:59')
and edu_video_upload.classifyOne = #{e.classifyOne}
and edu_video_upload.classifyTwo = #{e.classifyTwo}
and edu_video_upload.title like CONCAT('%',#{e.title},'%')
