select , user_user.nickname, user_user.email, edu_user_integral_template.name templateName, edu_user_integral_template.score templateScore, edu_user_integral_gift.name giftName, edu_user_integral_gift.course_id courseId from edu_user_integral_record left join user_user on edu_user_integral_record.user_id =user_user.id left join edu_user_integral_template on edu_user_integral_record.integral_type =edu_user_integral_template.id left join edu_user_integral_gift on edu_user_integral_record.other=edu_user_integral_gift.id and edu_user_integral_template.keyword='gift'
and edu_user_integral_record.user_id=#{e.userId}
and edu_user_integral_record.`integral_type` =#{e.integralType}
order by edu_user_integral_record.id desc

        	select count(1) from edu_user_integral_record


				  and   edu_user_integral_record.user_id=#{e.userId}


			      and    edu_user_integral_record.`integral_type` =#{e.integralType}

        SELECT ,
	        user_user.nickname,
	        user_user.email,
	        edu_user_integral_template.name templateName,
	        edu_user_integral_template.score templateScore,
	        edu_user_integral_gift.name giftName,
	        edu_user_integral_gift.course_id courseId
	        FROM edu_user_integral_record
	        LEFT JOIN user_user ON edu_user_integral_record.user_id =user_user.id
	        LEFT JOIN edu_user_integral_template ON edu_user_integral_record.integral_type =edu_user_integral_template.id
	        LEFT JOIN edu_user_integral_gift ON edu_user_integral_record.other=edu_user_integral_gift.id
	        WHERE edu_user_integral_record.`other`!=0 and edu_user_integral_record.integral_type=#{e.integralType}

	        and user_user.nickname=#{e.nickname}


	        and user_user.email=#{e.email}


	        and edu_user_integral_record.status=#{e.status}


			and edu_user_integral_record.create_time>=#{e.startDate}


			and edu_user_integral_record.create_time<=#{e.endDate} 

	        order by edu_user_integral_record.id desc

        select count(1) from edu_user_integral_record
          LEFT JOIN user_user ON edu_user_integral_record.user_id =user_user.id
	        LEFT JOIN edu_user_integral_template ON edu_user_integral_record.integral_type =edu_user_integral_template.id
	        LEFT JOIN edu_user_integral_gift ON edu_user_integral_record.other=edu_user_integral_gift.id
	        WHERE edu_user_integral_record.`other`!=0 and edu_user_integral_record.integral_type=#{e.integralType}

	        and user_user.nickname=#{e.email}


	        and user_user.email=#{e.email}


	        and edu_user_integral_record.status=#{e.status}


			and edu_user_integral_record.create_time>=#{e.startDate}


			and edu_user_integral_record.create_time<=#{e.endDate}

